mapper.select:
SELECT t.* FROM task AS t
INNER JOIN user_task AS ut ON t.task_id = ut.task_id
INNER JOIN user AS u ON ut.user_id = u.user_id

    INNER JOIN team_user ON u.user_id = team_user.user_id
    INNER JOIN team AS t2 ON team_user.team_id = t2.team_id


    INNER JOIN user_task_tag utt ON ut.ut_id = utt.ut_id
    INNER JOIN tag AS t3 ON utt.tag_id = t3.tag_id


WHERE u.user_id = #{userId}

    AND t.privilege = #{privilege}


    AND t.task_name = #{taskName}


    AND t.create_date = #{createDate}


    AND t.due_date = #{dueDate}


    AND t.type = #{type}


    AND t.status = #{status}


    AND t2.team_name = #{teamName}


    AND t3.tag_name = #{tag}


    AND t.father_task = #{fatherTask}
